/-- Need to use classical logic here because isPos and isNeg are not decidable -/
noncomputable abbrev abs (x : Real) : Real :=
  if x.IsPos then x else (if x.IsNeg then -x else 0)

/-- Proposition 5.4.7(e) (positive multiplication preserves order) / Exercise 5.4.2 -/
theorem mul_lt_mul_right {x y z : Real} (hxy : x < y) (hz : z.IsPos) : x * z < y * z := by rw [antisymm] at hxy ⊢;
  convert pos_mul hxy hz using 1; ring
