def mk {X : Type} [LE X] (hrefl : ∀ x : X, x ≤ x) (hantisymm : ∀ x y : X, x ≤ y → y ≤ x → x = y)
    (htrans : ∀ x y z : X, x ≤ y → y ≤ z → x ≤ z) : PartialOrder X :=
  { le := (· ≤ ·)
    le_refl := hrefl
    le_antisymm := hantisymm
    le_trans := htrans }

theorem iff' {X : Type} [PartialOrder X] (h : IsTotal X) :
    WellFoundedLT X ↔ ∀ A : Set X, A.Nonempty → ∃ x : A, IsMin x :=
  @iff X (LinearOrder.mk h)

def iso {X : Type} [PartialOrder X] : X ≃o OrderIdeals X :=
  { toFun x := ⟨.Iic x, by simp⟩
    invFun := by sorry
    left_inv := by sorry
    right_inv := by sorry
    map_rel_iff' := by sorry }
