theorem RS_of_extend {I J : BoundedInterval} (hIJ : I ⊆ J) {f : ℝ → ℝ} (h : PiecewiseConstantOn f I) {α : ℝ → ℝ}
    (hα : Monotone α) : PiecewiseConstantOn (fun x ↦ if x ∈ I then f x else 0) J := by sorry

/-- Theorem 11.8.8 (g) (Laws of RS integration) / Exercise 11.8.8 -/
theorem RS_integ_of_extend {I J : BoundedInterval} (hIJ : I ⊆ J) {f : ℝ → ℝ} (h : PiecewiseConstantOn f I) {α : ℝ → ℝ}
    (hα : Monotone α) : RS_integ (fun x ↦ if x ∈ I then f x else 0) J α = RS_integ f I α := by sorry

