@[coe]
def coe_of_fun {X Y : Set} (f : X → Y) : Object :=
  function_to_object X Y f

/-- Exercise 3.4.6 (i). One needs to provide a suitable definition of the power set here. -/
def powerset (X : Set) : Set :=
  (({0, 1} ^ X) : Set).replace (P := sorry) (by sorry)

/-- Lemma 3.4.10 -/
theorem exists_powerset (X : Set) : ∃ (Z : Set), ∀ x, x ∈ Z ↔ ∃ Y : Set, x = Y ∧ Y ⊆ X := by use powerset X;
  apply mem_powerset

/-- Connection with Mathlib indexed union -/
theorem iUnion_eq (I : Set) (A : I → Set) : (iUnion I A : _root_.Set Object) = ⋃ α, (A α : _root_.Set Object) := by ext;
  simp [mem_iUnion]
