/-- This equips an `OrderedPair` with proofs that `x ∈ X` and `y ∈ Y`. -/
def mk_cartesian {X Y : Set} (x : X) (y : Y) : X ×ˢ Y :=
  ⟨(⟨x, y⟩ : OrderedPair), by simp⟩

@[simp]
theorem coe_toNat {n : ℕ} (i : Fin n) : ((i : ℕ) : Object) = (i : Object) := by set j := (i : ℕ);
  obtain ⟨h, h' : i = Fin_mk n j h⟩ := toNat_spec i; rw [h']
