/-- Exercise 3.6.12 -/
def Permutations (n : ℕ) : Set :=
  (Fin n ^ Fin n).specify (fun F ↦ Function.Bijective (pow_fun_equiv F))

/-- Any `Fin n` can be cast to `Fin (n + 1)`. Compare to Mathlib `Fin.castSucc`. -/
def castSucc {n} (x : Fin n) : Fin (n + 1) :=
  Fin_embed _ _ (by omega) x

/-- Any natural `n` can be cast to `Fin (n + 1)`. Compare to Mathlib `Fin.last`. -/
def last (n : ℕ) : Fin (n + 1) :=
  Fin_mk _ n (by omega)
