type:
∀ (a b : Nat → Real),
  Eq
    (instHSub.hSub { m := 0, seq := fun n => ite (GE.ge n 0) (a n.toNat) 0, vanish := ⋯ }
      { m := 0, seq := fun n => ite (GE.ge n 0) (b n.toNat) 0, vanish := ⋯ })
    { m := 0, seq := fun n => ite (GE.ge n 0) ((fun n => instHSub.hSub (a n) (b n)) n.toNat) 0, vanish := ⋯ }

∀ (a : Nat → Real) (c : Real),
  Eq (instHSMul.hSMul c { m := 0, seq := fun n => ite (GE.ge n 0) (a n.toNat) 0, vanish := ⋯ })
    { m := 0, seq := fun n => ite (GE.ge n 0) ((fun n => instHMul.hMul c (a n)) n.toNat) 0, vanish := ⋯ }

∀ (a b : Nat → Real),
  Eq
    (instHAdd.hAdd { m := 0, seq := fun n => ite (GE.ge n 0) (a n.toNat) 0, vanish := ⋯ }
      { m := 0, seq := fun n => ite (GE.ge n 0) (b n.toNat) 0, vanish := ⋯ })
    { m := 0, seq := fun n => ite (GE.ge n 0) ((fun n => instHAdd.hAdd (a n) (b n)) n.toNat) 0, vanish := ⋯ }
